from typing import Union, Type

from kasasa_common.queue.queue_interface import QueueInterface
from kasasa_common.queue.memory_queue import MemoryQueue


def get_queue_interface(queue_type: str) -> Union[Type[QueueInterface], None]:
    """
    Returns the requested queue_type if it exists.

    Valid Values:
        'memory': MemoryQueue
    :param queue_type: str name of queue type, e.g. 'memory'
    :return:
    """
    queue_classes = dict(
        memory=MemoryQueue
    )
    return queue_classes.get(queue_type, None)
