import os
import shutil
from os import path as op


def makedirs_unless_exist(path: str):
    """Wrapper that will create a directory at the given path if it does not yet exist"""
    return os.makedirs(path, exist_ok=True)


def rmtree_if_exists(path: str):
    """Removes the filesystem object at the given path and it's members if it is a directory"""
    if op.exists(path):
        if op.isdir(path):
            shutil.rmtree(path)
        else:
            os.remove(path)
